mapper.insert:
insert into t_class (cid, cnum, cmajor, cfullname
  )
values (#{cid,jdbcType=INTEGER}, #{cnum,jdbcType=VARCHAR}, #{cmajor,jdbcType=VARCHAR}
  )

mapper.update:
update t_class
set cnum = #{cnum,jdbcType=VARCHAR},
  cmajor = #{cmajor,jdbcType=VARCHAR},
  cfullname = #{cfullname,jdbcType=VARCHAR}
where cid = #{cid,jdbcType=INTEGER}

mapper.select:
select cid, cnum, cmajor, cfullname
from t_class
where cid = #{cid,jdbcType=INTEGER}
