mapper.insert:
insert into t_department (did, dname, dgrade
  )
values (#{did,jdbcType=INTEGER}, #{dname,jdbcType=VARCHAR}, #{dgrade,jdbcType=VARCHAR}
  )

mapper.update:
update t_department
set dname = #{dname,jdbcType=VARCHAR},
  dgrade = #{dgrade,jdbcType=VARCHAR}
where did = #{did,jdbcType=INTEGER}

mapper.select:
select did, dname, dgrade
from t_department
where did = #{did,jdbcType=INTEGER}
