mapper.update:
update t_class
set cnum = #{cnum,jdbcType=VARCHAR},
  cdepartment = #{cdepartment,jdbcType=VARCHAR},
  cmajor = #{cmajor,jdbcType=VARCHAR},
  cgrade = #{cgrade,jdbcType=VARCHAR}
where cid = #{cid,jdbcType=INTEGER}

mapper.select:
select cid, cnum, cdepartment, cmajor, cgrade
from t_class
where cid = #{cid,jdbcType=INTEGER}
